new Swiper(".slider", {
  loop: true,
  speed: 1000,
  autoplay: {
    delay: 10000,
  },
  navigation: {
    nextEl: ".slider .btn-next",
    prevEl: ".slider .btn-prev",
  },
});

new Swiper(".items-slider", {
  loop: true,
  speed: 1000,
  slidesPerView: 1,
  autoplay: {
    delay: 10000,
  },
  navigation: {
    nextEl: ".items .btn-next",
    prevEl: ".items .btn-prev",
  },
  breakpoints: {
    // when window width is >= 320px
    320: {
      slidesPerView: 1,
      spaceBetween: 20
    },
    544: {
      slidesPerView: 2,
      spaceBetween: 20
    },
    1024: {
      slidesPerView: 3,
      spaceBetween: 20
    },
  }
});

new Swiper(".instagram-slider", {
  speed: 1000,
  // freeMode: true,
  breakpoints: {
    // when window width is >= 320px
    320: {
      slidesPerView: "auto",
      spaceBetween: 20
    },
    1200: {
      slidesPerView: 7,
      spaceBetween: 0
    },
  }
});

const productBottomSlider = new Swiper(".product-bottom", {
  slidesPerView: 3,
  watchSlidesProgress: true,
  breakpoints: {
    320: {
      spaceBetween: 10
    },
    1024: {
      spaceBetween: 20
    },
  }
});

var swiper2 = new Swiper(".product-top", {
  // allowTouchMove: false,
  effect: "fade",
  thumbs: {
    swiper: productBottomSlider,
  },
});

